@openhtf.PhaseOptions(requires_state=True)
@plugs.plug(update_kwargs=False, **monitor_plugs)
@measurements.measures(
    measurements.Measurement(measurement_name).WithUnits(
        units).WithDimensions(uom.MILLISECOND))
@functools.wraps(phase_desc.func)
def MonitoredPhaseFunc(test_state, *args, **kwargs):
  # Start monitor thread, it will run monitor_desc periodically.
  monitor_thread = _MonitorThread(
      measurement_name, monitor_desc, phase_desc.extra_kwargs, test_state,
      poll_interval_ms)
  monitor_thread.start()
  try:
    return phase_desc(test_state, *args, **kwargs)
  finally:
    monitor_thread.Kill()

@measures(
    Measurement('unset_dims').WithDimensions(units.HERTZ),
    Measurement('dimensions').WithDimensions(units.HERTZ),
    Measurement('lots_of_dims').WithDimensions(
        units.HERTZ, units.SECOND, units.RADIAN))
def dimensions(test):
  for dim in range(5):
    test.measurements.dimensions[dim] = 1 << dim
  for x, y, z in zip(range(1, 5), range(21, 25), range (101, 105)):
    test.measurements.lots_of_dims[x, y, z] = x + y + z

def attachments(test):
  test.attach('test_attachment', 'This is test attachment data.')
  test.attach_from_file('example_attachment.txt')
